  -- Q1: Circular Motion - Work done by centripetal force
  INSERT INTO public.test_section_questions (section_id, test_id, question_number, question_text, options, correct_answer, marks, negative_marks, difficulty, solution_text, order_index)
  VALUES (
    v_physics_mcq_section_id, v_test_id, 1,
    'A particle of mass $m$ moves in a circular path of radius $r$ with uniform speed $v$. The work done by the centripetal force in one complete revolution is:',
